mapper.update:
UPDATE empresas 
SET fotografia=#{foto} 
WHERE idPaciente=#{idPaciente}

mapper.select:
SELECT idPaciente,nombre,apellidoPaterno,ApellidoMaterno,TO_BASE64(fotografia) as 'fotografiaBase64' 
FROM empresas 
WHERE idPaciente=#{idPaciente}

mapper.update:
UPDATE empresas SET 
nombre=#{nombre}, nombreComercial=#{nombreComercial}, logo=#{logo}, representanteLegal=#{representanteLegal}, 
email=#{email}, telefono=#{telefono}, paginaWeb=#{paginaWeb}, RFC=#{RFC}, estatus=#{estatus}, idDireccion=#{idDireccion} 
WHERE idEmpresa=#{idEmpresa}

UPDATE empresas 
SET logo=#{logo} 
WHERE idEmpresa=#{idEmpresa}

mapper.select:
SELECT idEmpresa, nombre, nombreComercial, TO_BASE64(logo) as 'logoBase64' 
FROM empresas 
WHERE idEmpresa=#{idEmpresa}
