mapper.update:
UPDATE sucursales SET 
nombre=#{nombre}, telefono=#{telefono}, latitud=#{latitud}, longitud=#{longitud}, nombreEncargado=#{nombreEncargado}, 
idEmpresa=#{idEmpresa}, idDireccion=#{idDireccion}
WHERE idSucursal=#{idSucursal}

UPDATE sucursales 
SET logo=#{logo} 
WHERE idSucursal=#{idSucursal}

mapper.select:
SELECT idSucursal, nombre, TO_BASE64(logo) as 'logoBase64' 
FROM sucursales 
WHERE idSucursal=#{idSucursal}
